package Arrays;

import java.util.Arrays;

public class ConvertStringToArray {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		String s1 = "sarah";
		char [] c1 = s1.toCharArray();
		System.out.println(Arrays.toString(c1));
		Arrays.sort(c1);
		System.out.println(Arrays.toString(c1));
		
		
	}

}
